(()=>{const n=$(".comic")[0].id;$("#btnAddToCard").click((function(n){n.preventDefault(),t(1)}));const t=t=>{const e=new XMLHttpRequest;e.open("GET","/src/php/functions/cardItems.php?id="+n+"&amount="+t,!0),e.onload=function(){const n=document.getElementById("cardAmount");if(""==n.innerHTML)n.innerHTML=t;else{const e=parseInt(n.innerHTML)+parseInt(t);n.innerHTML=e}},e.send()}})();